import React from 'react';
import { connect } from 'react-redux';
import BookSection from '../../components/BookSection/BookSection';
import './HomePage.scss';

const homePage = ({ homepageBooks }) => {
  return (
    <div>
      <BookSection books={homepageBooks.newBooks} title="New Arrivals" />
      <BookSection books={homepageBooks.bestPicks} title="Our Picks" />
      <BookSection books={homepageBooks.kidsBooks} title="Books for Kids" />
    </div>

  );
};

const mapStateToProps = state => ({
  homepageBooks: state.book.homepageBooks,
});

export default connect(mapStateToProps)(homePage);
