import React from 'react';
import CategoryFilter from '../CategoryFilter/CategoryFilter';
import SingleBook from '../SingleBook/SingleBook';
import './BookSection.scss';

const bookSection = ({ books, title, showFilter }) => {
  const renderCategoryFilter = showFilter ? <CategoryFilter /> : <span className="section__link">See all</span>;
  return (
    <section className="section">
      <div className="section__header">
        <span className="section__title">{title}</span>
        {
          renderCategoryFilter
        }
      </div>
      <div className="section__books">
        {
          books.map(item => (<SingleBook key={item.id} book={item} />))
        }
      </div>
    </section>
  );
}

export default bookSection;
