import React, { Component } from 'react';
import { BrowserRouter, Route, Switch } from 'react-router-dom';
import HomePage from './container/HomePage/HomePage';
import AdminMainContainer from './container/AdminContainers/AdminMainContainer/AdminMainContainer';
import SigninSignup from './components/Auth/SigninSignup/SigninSignup';
import classes from './App.module.css';

class App extends Component {
  unSubscribeFromAuth = null;

  constructor() {
    super();
    this.state = {
      auth: '',
      currentUser: '',
    };
  }

  componentDidMount() {
    this.unSubscribeFromAuth = auth.onAuthStateChanged(user => {
      this.setState({ currentUser: user });
      console.log(user);
    });
  }

  componentWillUnmount() {
    this.unSubscribeFromAuth();
  }
  render() {
    const { auth } = this.state;
    return (
      <BrowserRouter>
        <div className={classes.App}>
          <Switch>
            <Route path="/auth" exact component={auth ? AdminMainContainer : HomePage} />
            <Route path="/register" exact component={SigninSignup} />
          </Switch>
        </div>
      </BrowserRouter>
    );
  }
}

export default App;
