const bookReducer = (state = [], action) => {
  switch (action.type) {
    case ('CREATE_BOOK'):
      console.log(action.book);
      return [...state, action.book];

    case ('REMOVE_BOOK'):
      return {
        ...state,
        books: state.books.filter(result => result.id !== action.book.id),
      };
    default:
      return state;
  }
};

export default bookReducer;
