import React, { Component } from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { createBook } from '../../../actions/index';
import classes from './BooksForm.module.css';

class BookForm extends Component {
  constructor() {
    super();
    this.state = {
      book: {
        isbn: null,
        title: '',
        description: '',
        category: '',
        genre: '',
        author: '',
        publisher: '',
        price: '',
        image: '',
      },
    };
  }

  handleChange = event => {
    const { book } = this.state;
    const updatedBook = book;
    updatedBook[event.target.id] = event.target.value;
    this.setState({ book: updatedBook });
  };

  handleSubmit = () => {
    const { addBook } = this.props;
    const book = { ...this.state, id: Math.random() };
    addBook(book);
    this.setState({ title: '', category: '' });
  };

  render() {
    const { isbn, title, description, category, genre, author, publisher, price, image } = this.state;
    const { categories } = this.props;
    return (
      <div className={classes.Form}>
        <form>
          <h3 className={classes.Title}>Add new to Bookstore</h3>
          <input type="number" id="isbn" placeholder="ISBN number" value={isbn} onChange={this.handleChange} required />
          <input className={[classes.Input, classes.Text].join(' ')} id="title" placeholder="Enter Book Title" type="text" value={title} onChange={this.handleChange} required />
          <input type="textarea" id="description" placeholder="Add short description" value={description} onChange={this.handleChange} required />
          <select className={[classes.Select, classes.Text].join(' ')} id="category" value={category} onChange={this.handleChange}>
            {
              categories.map(option => (
                <option
                  key={option}
                  value={option}
                >
                  {option}
                </option>
              ))
            }
          </select>
          <input type="text" id="genre" placeholder="Add genre" value={genre} onChange={this.handleChange} required />
          <input type="text" id="author" placeholder="Author please" value={author} onChange={this.handleChange} required />
          <input type="text" id="publisher" placeholder="Publisher name" value={publisher} onChange={this.handleChange} required />
          <button className={classes.Button} name="add" type="button" onClick={this.handleSubmit}>ADD</button>
        </form>
      </div>
    );
  }
}

const mapDispatchToProps = dispatch => ({
  addBook: data => { dispatch(createBook(data)); },
});

BookForm.propTypes = {
  addBook: PropTypes.func.isRequired,
  categories: PropTypes.instanceOf(Array).isRequired,
};

export default connect(null, mapDispatchToProps)(BookForm);
