import React from 'react';
import './CrudButtons.scss';

const selectedBookHandler = id => {
  history.push(`/auth/book/${id}`);
}

const editSelectedBookHandler = id => {
  history.push(`/auth/book/edit/${id}`);
}

const crudButtons = ({ clicked, selectedBook }) => {
  return (
    <div className="Btns">
      <button type="button" id={selectedBook.id} onClick={selectedBook => clicked(selectedBook)} className="btn delete">Remove</button>
      <button type="button" className="btn show" onClick={() => selectedBookHandler(selectedBook.id)}>Show</button>
      <button type="button" className="btn edit" onClick={() => editSelectedBookHandler(selectedBook.id)}>Edit</button>
    </div>
  );
};

export default crudButtons;
