import * as actionTypes from '../actions/actionTypes';
import { updateBookState } from '../../utilities/utility';

const initialState = {
  books: {},
  homepageBooks: {},
  currentBook: '',
  categoryFilter: 'all',
  filteredBooks: [],
};

const bookReducer = (state = initialState, action) => {
  switch (action.type) {
    case actionTypes.GET_BOOKS:
      return {
        ...state,
        books: action.books,
        homepageBooks: action.arrangedBooks,
      };
    case actionTypes.FILTER_CATEGORY:
      return {
        ...state,
        filteredBooks: updateBookState(state.books, action.filter),
        categoryFilter: action.filter,
      };
    case actionTypes.BOOK_FILTER:
      return {
        ...state,
        currentBook: state.books[action.bookID],
      };
    default:
      return state;
  }
};


export default bookReducer;
