const category = ['Action', 'Biography', 'History', 'Horror', 'Kids', 'Learning', 'Sci-Fi'];
const bookStatus = ['brand new', 'used'];
const updateHomePageStore = availableBooks => {
  const shuffled = availableBooks.sort(() => 0.5 - Math.random());
  const selected = shuffled.slice(0, 6);
  return selected;
};


const immutabiityExample = () => {
  const values = [
    [2, 4, 3],
    [4, 4, 4],
    [2, 2, 2],
    [3, 3, 3],
  ];
  const doubleFruits = values.forEach(fruit => {
    return {
      ...fruit,
    };
  });
  return doubleFruits;
};

export { category, bookStatus, updateHomePageStore, immutabiityExample };
